const body = $("Webhook").first().json.body ?? {};
const ingredients = Array.isArray(body.ingredients) ? body.ingredients : [];
const servings = Number(body.servings ?? 2);
const preferences = body.preferences ?? {};
const helpers = body.helpers ?? {};
const helpersCount = Math.max(1, Math.min(3, Number(helpers.count ?? 1)));
const cookingTime = preferences.cookingTime ?? "Medium";
const cuisine = preferences.cuisine ?? "Fusion";
const diet = preferences.diet ?? "None";
const inputIngredients = ingredients
  .map((s) => String(s).trim())
  .filter(Boolean);

const prompt = `
You are a recipe generator. Output ONLY valid JSON. No markdown.

INPUT:
- ingredients: ${JSON.stringify(inputIngredients)}
- servings: ${servings}
- timeCategory: ${cookingTime}
- cuisine: ${cuisine}
- diet: ${diet}
- helpersCount: ${helpersCount}

HARD RULES:
0) Language: English only for all step titles and descriptions.
   Exception: keep original dish/ingredient names as‑is; do not translate them.
1) Generate exactly 3 recipes.
2) Each recipe must use at least 70% of the INPUT ingredients.
3) Each recipe may contain at most 3 missingBasics.
4) Each recipe must have between 4 and 6 steps.
5) Split helper tasks among helpers 1..helpersCount.
6) Each step MUST have:
   - a short title (2–6 words)
   - a longer description (2–4 sentences, 45–80 words).

OUTPUT JSON SCHEMA:
{
  "recipes": [
    {
      "title": "string",
      "ingredientsUsed": ["string"],
      "missingBasics": [
  { "name": "string", "amount": 0, "unit": "string" }
],
      "steps": [
        {
          "title": "short step title, 2-6 words",
          "description": "detailed step description, 2-4 sentences, 45-80 words"
        }
      ],
      "helperTodos": [
        { "helper": 1, "tasks": ["string (max 8 words each)"] }
      ]
    }
  ]
}

IMPORTANT FORMATTING RULES:
- Return EXACTLY one JSON object.
- Generate exactly 3 recipes.
- If you cannot comply, return exactly:
{ "recipes": [] }
- The first character MUST be '{' and the last character MUST be '}'.
- Do NOT include any explanation, comments or markdown.
- All step descriptions must be plain text (no line breaks, no bullet points).

Remember: exactly 3 recipes, valid JSON only.
`;

return [
  {
    json: {
      prompt,
      meta: {
        inputIngredients,
        servings,
        cookingTime,
        cuisine,
        diet,
        helpersCount,
      },
    },
  },
];

const reason = $json.reason ?? "UNKNOWN";
const llmRaw = $json.llmRaw ?? "";
const meta = $node["PreparePrompt"].json.meta;
const prompt = `
Fix the JSON to satisfy the HARD RULES. Output ONLY valid JSON.

Reason for failure: ${reason}

Original INPUT ingredients: ${JSON.stringify(meta.inputIngredients)}
servings: ${meta.servings}
timeCategory: ${meta.cookingTime}
cuisine: ${meta.cuisine}
diet: ${meta.diet}
helpersCount: ${meta.helpersCount}

Previous invalid output:
${llmRaw}

Return corrected JSON with exactly 3 recipes.
Language: English only for titles and descriptions. Keep dish names/ingredients as-is.
Descriptions must be 2–4 sentences (45–80 words); do not translate names.
`;

return [{ json: { prompt } }];

