function getText(j) {
  if (!j) return "";
  if (typeof j.response === "string") return j.response;
  if (typeof j.output_text === "string") return j.output_text;
  const t = j.output?.[0]?.content?.[0]?.text;
  if (typeof t === "string") return t;
  if (typeof j.text === "string") return j.text;
  if (typeof j.content === "string") return j.content;
  if (typeof j.message?.content === "string") return j.message.content;
  return "";
}

const raw = getText($json);
const meta = $node["PreparePrompt"].json.meta;
let jsonOnly = raw;
const start = raw.indexOf("{");
const end = raw.lastIndexOf("}");
if (start >= 0 && end >= start) jsonOnly = raw.slice(start, end + 1);
jsonOnly = jsonOnly.replace(/,\s*}/g, "}").replace(/,\s*]/g, "]");
let parsed;
try {
  parsed = JSON.parse(jsonOnly);
} catch {
  return [{ json: { valid: false, reason: "JSON_PARSE_FAILED", llmRaw: raw } }];
}
if (parsed.Recipes && !parsed.recipes) {
  parsed.recipes = parsed.Recipes;
  delete parsed.Recipes;
}

const synonymMap = {
  reis: ["reis", "rice"],
  tofu: ["tofu"],
  sojasauce: ["sojasauce", "soy sauce", "soya sauce"],
  zwiebel: ["zwiebel", "onion"],
  knoblauch: ["knoblauch", "garlic"],
  ingwer: ["ingwer", "ginger"],
  butter: ["butter"],
  zucker: ["zucker", "sugar"],
  mehl: ["mehl", "flour"],
  salz: ["salz", "salt"],
  pfeffer: ["pfeffer", "pepper"],
  öl: ["öl", "oil"],
  tomaten: ["tomaten", "tomatoes", "tomato"],
  huhn: ["huhn", "chicken"],
  rindfleisch: ["rindfleisch", "beef"],
  schwein: ["schwein", "pork"],
  kartoffeln: ["kartoffeln", "potatoes", "potato"],
  sahne: ["sahne", "cream"],
  milch: ["milch", "milk"],
};

const recipes = Array.isArray(parsed.recipes) ? parsed.recipes : [];
if (recipes.length !== 3) {
  return [
    {
      json: {
        valid: false,
        reason: "NOT_EXACTLY_3_RECIPES",
        got: recipes.length,
        llmRaw: raw,
      },
    },
  ];
}

const inputSet = new Set();
for (const ingRaw of meta.inputIngredients ?? []) {
  const ing = String(ingRaw).toLowerCase().trim();
  (synonymMap[ing] ?? [ing]).forEach((s) => inputSet.add(s));
}
const minRequired = Math.ceil((meta.inputIngredients?.length ?? 0) * 0.7);

function normalize(x) {
  return String(x ?? "")
    .toLowerCase()
    .trim();
}

const MIN_STEP_COUNT = 4;
const MAX_STEP_COUNT = 6;
const germanMarkers = [
  " anrichten",
  " servieren",
  " vorbereiten",
  " vorbereitung",
  " zubereiten",
  " würzen",
  " schneiden",
  " braten",
  " kochen",
  " backen",
  " köcheln",
  " fertig",
  " machen",
  " zutaten",
  " rühren",
  " erhitzen",
  " pfanne",
  " topf",
  " minuten",
  " und ",
  " mit ",
  " für ",
  " auf ",
  " bei ",
  " dann ",
  "ä",
  "ö",
  "ü",
  "ß",
];

const MIN_DESC_CHARS = 120;

for (let idx = 0; idx < recipes.length; idx++) {
  const r = recipes[idx] ?? {};
  const steps = Array.isArray(r.steps) ? r.steps : [];
  if (steps.length < MIN_STEP_COUNT) {
    return [
      {
        json: {
          valid: false,
          reason: "TOO_FEW_STEPS",
          idx,
          stepsCount: steps.length,
          llmRaw: raw,
        },
      },
    ];
  }

  if (steps.length > MAX_STEP_COUNT) {
    return [
      {
        json: {
          valid: false,
          reason: "TOO_MANY_STEPS",
          idx,
          stepsCount: steps.length,
          llmRaw: raw,
        },
      },
    ];
  }

  const stepBlob = steps
    .map((s) => {
      if (s && typeof s === "object")
        return `${s.title ?? ""} ${s.description ?? ""}`;
      return String(s ?? "");
    })
    .join(" ")
    .toLowerCase();
  if (germanMarkers.some((w) => stepBlob.includes(w))) {
    return [
      {
        json: { valid: false, reason: "NON_ENGLISH_STEPS", idx, llmRaw: raw },
      },
    ];
  }

  const tooShort = steps.some((s) => {
    const d =
      s && typeof s === "object"
        ? String(s.description ?? "")
        : String(s ?? "");
    return d.trim().length < MIN_DESC_CHARS;
  });
  if (tooShort) {
    return [
      { json: { valid: false, reason: "STEPS_TOO_SHORT", idx, llmRaw: raw } },
    ];
  }

  const ingredientsUsed = Array.isArray(r.ingredientsUsed)
    ? r.ingredientsUsed
    : [];
  const usedFromInput = ingredientsUsed
    .map(normalize)
    .filter((x) => inputSet.has(x));
  const uniqueUsed = [...new Set(usedFromInput)];
  if (uniqueUsed.length < minRequired) {
    return [
      {
        json: {
          valid: false,
          reason: "INGREDIENT_RATIO_TOO_LOW",
          idx,
          usedCount: uniqueUsed.length,
          minRequired,
          llmRaw: raw,
        },
      },
    ];
  }

  const hc = Number(meta.helpersCount ?? 1);
  if (hc < 1 || hc > 3) {
    return [
      {
        json: {
          valid: false,
          reason: "INVALID_HELPERS_COUNT_META",
          hc,
          llmRaw: raw,
        },
      },
    ];
  }
}

function splitStepText(step) {
  const text = String(step ?? "").trim();
  if (!text) return { title: "", body: "" };
  const parts = text.split(":");
  if (parts.length === 2 && parts[0].length < 40) {
    return { title: parts[0].trim(), body: parts[1].trim() };
  }
  const andIndex = text.toLowerCase().indexOf(" und ");
  if (andIndex > 0 && andIndex < text.length - 3) {
    return {
      title: text.slice(0, andIndex).trim(),
      body: text.slice(andIndex + 4).trim(),
    };
  }
  const words = text.split(" ");
  if (words.length > 3) {
    const title = words.slice(0, 3).join(" ");
    const body = words.slice(3).join(" ");
    return { title, body };
  }
  return { title: text, body: "" };
}

function buildDisplayTags(meta) {
  const tags = [];
  if (meta.diet && meta.diet !== "None") {
    tags.push(meta.diet);
  }
  if (meta.cookingTime) {
    tags.push(meta.cookingTime);
  }
  return tags;
}

const now = new Date().toISOString();

const NUTRITION_BASE = {
  energyKcal: 630,
  proteinG: 18,
  fatG: 24,
  carbsG: 58,
};

function buildNutrition(servings) {
  const s = Math.max(1, Math.floor(Number(servings) || 1));

  const byPortion = Array.from({ length: s }, (_, i) => {
    const mult = i + 1;
    return {
      portion: mult,
      energyKcal: Math.round(NUTRITION_BASE.energyKcal * mult),
      proteinG: round1(NUTRITION_BASE.proteinG * mult),
      fatG: round1(NUTRITION_BASE.fatG * mult),
      carbsG: round1(NUTRITION_BASE.carbsG * mult),
    };
  });

  return {
    perServing: { ...NUTRITION_BASE },
    byPortion,
    total: byPortion[s - 1],
  };
}

return recipes.map((r, index) => {
  const rawSteps = Array.isArray(r.steps) ? r.steps : [];
  const stepTitles = [];
  const stepBodies = [];
  const flatSteps = [];

  for (const s of rawSteps) {
    if (s && typeof s === "object") {
      const t = String(s.title ?? "").trim();
      const d = String(s.description ?? "").trim();
      const title = t || (d ? splitStepText(d).title : "");
      const body = d || (t ? splitStepText(t).body : "");
      stepTitles.push(title);
      stepBodies.push(body);
      flatSteps.push(body || title);
    } else {
      const text = String(s ?? "").trim();
      const { title, body } = splitStepText(text);
      stepTitles.push(title);
      stepBodies.push(body);
      flatSteps.push(body || title);
    }
  }

  function parseBasic(raw) {
    const s = String(raw ?? "").trim();
    if (!s) return null;
    let m = s.match(/^(\d+(?:[.,]\d+)?)\s*([a-zA-ZäöüÄÖÜ]+)\s+(.+)$/);
    if (m) {
      return {
        amount: Number(m[1].replace(",", ".")),
        unit: m[2],
        name: m[3].trim(),
        raw: s,
      };
    }
    m = s.match(/^(.+?)\s+(\d+(?:[.,]\d+)?)\s*([a-zA-ZäöüÄÖÜ]+)$/);
    if (m) {
      return {
        name: m[1].trim(),
        amount: Number(m[2].replace(",", ".")),
        unit: m[3],
        raw: s,
      };
    }
    return { name: s, raw: s };
  }

  function normalizeMissingBasics(arr) {
    const list = Array.isArray(arr) ? arr : [];
    return list
      .map((x) => {
        if (x && typeof x === "object") {
          const name = String(x.name ?? "").trim();
          if (!name) return null;
          const amount =
            x.amount == null || x.amount === ""
              ? undefined
              : Number(String(x.amount).replace(",", "."));
          const unit = x.unit != null ? String(x.unit).trim() : undefined;

          return {
            name,
            amount: Number.isFinite(amount) ? amount : undefined,
            unit: unit || undefined,
            raw: typeof x.raw === "string" ? x.raw : undefined,
          };
        }
        return parseBasic(x);
      })
      .filter(Boolean)
      .filter(
        (v, i, a) =>
          a.findIndex(
            (t) =>
              t.name === v.name && t.amount === v.amount && t.unit === v.unit,
          ) === i,
      );
  }

  function stripUndefined(obj) {
    if (Array.isArray(obj)) {
      return obj.map(stripUndefined);
    }
    if (obj && typeof obj === "object") {
      return Object.fromEntries(
        Object.entries(obj)
          .filter(([_, v]) => v !== undefined)
          .map(([k, v]) => [k, stripUndefined(v)]),
      );
    }
    return obj;
  }

  function finalizeMissingBasics(arr) {
    let basics = stripUndefined(normalizeMissingBasics(arr));
    basics = basics.filter(
      (b) => b && b.name && (b.amount == null || b.amount > 0),
    );
    const byName = new Map(
      basics.map((b) => [String(b.name).toLowerCase().trim(), b]),
    );
    const wanted = [
      { key: "salt", def: { name: "salt", amount: 1, unit: "pinch" } },
      { key: "pepper", def: { name: "pepper", amount: 1, unit: "pinch" } },
      { key: "oil", def: { name: "oil", amount: 1, unit: "tbsp" } },
    ];
    const result = wanted.map(({ key, def }) => {
      const existing = byName.get(key);
      if (!existing) return def;
      return {
        name: def.name,
        amount:
          existing.amount != null && existing.amount > 0
            ? existing.amount
            : def.amount,
        unit: existing.unit ? existing.unit : def.unit,
        ...(existing.raw ? { raw: existing.raw } : {}),
      };
    });
    return result;
  }

  return {
    json: {
      title: (r.title || "").trim() || `Recipe ${index + 1}`,
      ingredientsUsed:
        Array.isArray(r.ingredientsUsed) && r.ingredientsUsed.length
          ? r.ingredientsUsed
          : meta.inputIngredients,
      missingBasics: finalizeMissingBasics(r.missingBasics),
      steps: flatSteps,
      stepTitles,
      stepBodies,
      helperTodos: Array.isArray(r.helperTodos) ? r.helperTodos : [],
      servings: Number(meta.servings),
      energyKcal: NUTRITION_BASE.energyKcal,
      proteinG: NUTRITION_BASE.proteinG,
      fatG: NUTRITION_BASE.fatG,
      carbsG: NUTRITION_BASE.carbsG,
      createdAt: now,
      likes: 0,
      tags: buildDisplayTags(meta),
      timeCategory: meta.cookingTime ?? null,
      cuisine: meta.cuisine ?? null,
      diet: meta.diet ?? null,
      helpersCount:
        meta.helpersCount != null ? Number(meta.helpersCount) : null,
    },
  };
});

