    return {
      portion: mult,
      energyKcal: Math.round(NUTRITION_BASE.energyKcal * mult),
      proteinG: round1(NUTRITION_BASE.proteinG * mult),
      fatG: round1(NUTRITION_BASE.fatG * mult),
      carbsG: round1(NUTRITION_BASE.carbsG * mult),
    };
  });

  return {
    perServing: { ...NUTRITION_BASE },
    byPortion,
    total: byPortion[s - 1],
  };
}

